import java.util.*;public class Main{    public static void main(String[] args){        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        while(t-->0){            int n=sc.nextInt();            int a=sc.nextInt();            int less=0;            int more=0;            for(int i=0;i<n;i++){                int temp=sc.nextInt();                if(temp<a){                    less++;                }                else if(temp>a){                    more++;                }                else{                    continue;                }                            }            if(less>more){                System.out.println(a-1);            }            else{                System.out.println(a+1);            }        }    }}