import java.util.*;public class Main{    public static void main(String[] args){        Scanner sc = new Scanner(System.in);        int x=0;        int num=sc.nextInt();        while(num-->0){            String str = sc.next();            if(str.contains("++")){                x+=1;            }            else{                x-=1;            }        }        System.out.println(x);    }}